import React, { useEffect, useRef } from 'react';
import styled from 'styled-components';
import axios from 'axios';

const LineGraph = (props) => {
    let userAssessmentData;

    // REQUEST: Retrieve user's assessment history.
    const getAssessmentData = async () => {
        const numOfEntries = 4;
        const storedUserId = localStorage.getItem('userId');
        const response =  await axios.get(`http://localhost:4000/api/getArchiveScore/${storedUserId}/${numOfEntries}`);
        userAssessmentData = response.data;
        console.log(userAssessmentData);

    };

    // VARIABLES: Line Graph Container Properties
    const containerHeight = 400;
    const containerWidth = 400;
    const datesWidth = 60;

    // VARIABLES: Line Graph Mock Data
    const componentData = [
        {y: 0},
        {y: 23},
        {y: 5},
        {y: 20},
    ];
    const datesData = [
        {dd: '08', mm: '04'},
        {dd: '09', mm: '05'},
        // {dd: '10', mm: '06'},
        // {dd: '11', mm: '07'},
    ];
    const componentSize = [(containerWidth*.85), 250]; // (x, y) or (width, height)
    const componentSections = 4; // Number of entries.
    const scoreRange = [0, 50]; // (Minimum Score, Maximum Score)

    // STYLE: Component background.
    const lineGraphBackgroundStyle = {
        position: 'absolute',
        left: '-0%',
        marginTop: '50px',
        height: `${containerHeight}px`,
        width: `${containerWidth}px`,
        borderRadius: '25px',
        backgroundColor: 'rgb(235,235,235)',
    };

    return (
        <LineGraphContainer>
            <div className="LineGraph-Background" style={lineGraphBackgroundStyle}>
                {/* SUB-COMPONENT Part 1A/B */}
                <LineGraphScoreAndComponent scoreRange={scoreRange} componentData={componentData} componentSize={componentSize} componentSections={componentSections} datesWidth={datesWidth}/>
                {/* SUB-COMPONENT Part 2 */}
                <LineGraphDates datesData={datesData} datesWidth={datesWidth}/>
            </div>
        </LineGraphContainer>
    );
};

// STYLE: Parent container.
const LineGraphContainer = styled.div`
    display: flex;
    flex-direction: column;
    overflow: hidden;
    p {
        user-select: none;
        color: rgb(20,20,20);
        z-index: 9999;
    }
`;

// SUB-COMPONENT: Part 1 - Combines y-axis and line graph component.
const LineGraphScoreAndComponent = (props) => {

    const range = props.scoreRange;
    const size = props.componentSize;
    const data = props.componentData;
    const sections = props.componentSections;
    const width = props.datesWidth;

    return(
        <div className="LineGraph-Content-Row-1" style={{height: `${size[1]}px`, display: 'flex', flexDirection: 'row', justifyContent: 'center', marginTop: '50px'}}>
            <LineGraphScore scoreRange={range}/>
            <LineGraphComponent data={data} size={size} sections={sections} scoreRange={range} width={width}/>
        </div>
    );
};

// SUB-COMPONENT: Part 1A - Line graph component.
const LineGraphComponent = (props) => {

    // VARIABLES: Line Graph Display Properties
    const canvasRef = useRef(null);
    const width = props.size[0] - props.width;
    const height = props.size[1];
    const sections = props.sections;
    const intervals = width / (sections - 1);
    const maxScore = Math.max(...props.scoreRange);
    const incomingData = props.data;

    // STYLE: Line Graph Component Stylings
    const lineGraphDisplayStyle = {
        flex: '0 0 85%',
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        backgroundColor: 'blue',
        zIndex: '9999',
    };
    const lineGraphStyle = {
        width: `${width}`,
        // border: '1px solid #ccc',
    };

    // GRAPH: Line Graph Creation.
    useEffect(() => {
        const canvas = canvasRef.current;
        const ctx = canvas.getContext('2d');

        var outgoingData = incomingData.map((entry, index) => ({
            x: index * intervals,
            y: height - (height * entry.y / maxScore),
        }));

        // const maxY = Math.max(...outgoingData.map(entry => entry.y));
        // console.log(`Max Number: ${maxY}`);

        ctx.clearRect(0, 0, width, height);
        ctx.beginPath();
        ctx.moveTo(outgoingData[0].x, outgoingData[0].y);
        console.log(`(x${0}, y${0}): (`, outgoingData[0].x, ',' ,outgoingData[0].y, ')');

        for (let i = 1; i < outgoingData.length; i++) {
            ctx.lineTo(outgoingData[i].x, outgoingData[i].y);
            console.log(`(x${i}, y${i}): (`, outgoingData[i].x, ',' ,outgoingData[i].y, ')');
        }

        ctx.strokeStyle = 'red';
        ctx.lineWidth = 5;
        ctx.stroke();

        ctx.beginPath();
        ctx.moveTo(outgoingData[0].x, outgoingData[0].y-30);
        console.log(`(x${0}, y${0}): (`, outgoingData[0].x, ',' ,outgoingData[0].y, ')');

        for (let i = 1; i < outgoingData.length; i++) {
            ctx.lineTo(outgoingData[i].x, outgoingData[i].y-30);
            console.log(`(x${i}, y${i}): (`, outgoingData[i].x, ',' ,outgoingData[i].y, ')');
        }

        ctx.strokeStyle = 'green';
        ctx.lineWidth = 5;
        ctx.stroke();
    }, [width, height, intervals, incomingData]);

    return (
        <div className="LineGraph-Display" style={lineGraphDisplayStyle}>
            <canvas ref={canvasRef} width={width} height={height} style={lineGraphStyle} />
        </div>
    );
};

// SUB-COMPONENT: Part 1B - The y-axis.
const LineGraphScore = (props) => {

    const scoreRange = props.scoreRange;
    const scoreRangeMax = Math.max(...scoreRange);
    const scoreRangeMin = Math.min(...scoreRange);
    const scoreInterval = (scoreRangeMax-scoreRangeMin)/5

    return (
        <LineGraphScoreStyled>
            <p id='LineGraphScoreTop'>{scoreRangeMax}</p>
            <p>{scoreInterval*4}</p>
            <p>{scoreInterval*3}</p>
            <p>{scoreInterval*2}</p>
            <p>{scoreInterval*1}</p>
            <p id='LineGraphScoreBottom'>{scoreRangeMin}</p>
        </LineGraphScoreStyled>
    );
};

// STYLE: Part 1B - The y-axis stylings.
const LineGraphScoreStyled = styled.div`
    display: flex;
    flex-direction: column;
    flex: 0 0 10%;
    justify-content: space-between;
    background-color: pink;
    p {
        font-size: 10pt;
        text-align: right;
        margin: 0px;
        /* background-color: yellow; */
    }
`;

// SUB-COMPONENT: Part 2 - The x-axis.
const LineGraphDates = (props) => {

    const dates = props.datesData;
    const width = props.datesWidth;

    const checkDate = (index) => !!dates[index];

    return (
        <div className="LineGraph-Content-Row-2" style={{ display: 'flex', flexDirection: 'row', justifyContent: 'center' }}>
            <LineGraphDateStyled datesWidth={width}>
                <p>{ checkDate(0) ? `${ dates[0].dd }/${ dates[0].mm }` : 'MM/DD' }</p>
                <p>{ checkDate(1) ? `${ dates[1].dd }/${ dates[1].mm }` : 'MM/DD' }</p>
                <p>{ checkDate(2) ? `${ dates[2].dd }/${ dates[2].mm }` : 'MM/DD' }</p>
                <p>{ checkDate(3) ? `${ dates[3].dd }/${ dates[3].mm }` : 'MM/DD' }</p>
            </LineGraphDateStyled>
            <div style={{ flex: '0 0 2.5%'}}/>
        </div>
    );
};

//STYLE: Part 2 - The x-axis stylings.
const LineGraphDateStyled = styled.div`
    flex: 0 0 85%;
    display: flex;
    flex-direction: row;
    justify-content: space-between;
    margin-left: auto;
    /* background-color: orange; */
    p {
        text-align: right;
        font-size: 10pt;
        width: ${(props) => props.datesWidth}px;
        margin: 0px;
        margin-top: 25px;
        background-color: red;
        transform: rotate(-0deg);
    }
`;

export default LineGraph;
