import React, { useEffect, useRef } from 'react';
import styled from 'styled-components';

const LineGraph = (props) => {
    const data = [
        {y: 0},
        {y: 50},
        {y: 65},
        {y: 100},
    ];
    const size = [250, 250]; // (x, y) or (width, height)
    const sections = 4; // Number of entries.
    const scoreRange = [0, 50]; // (Minimum Score, Maximum Score)
    console.log(size[1]);
    return (
        <div id="LineGraph-Container">
            <div className="LineGraph-Background">
            <div className="LineGraph-Content">
                <div className="LineGraph-Content-Row-1" style={`height: ${size[1]}px`}>
                    <LineGraphScores scoreRange={scoreRange}/>
                <div className="LineGraph-Display">
                    <LineGraphComponent data={data} size={size} section={sections}/>
                </div>
                </div>
                <div className="LineGraph-Content-Row-2">
                <div className="LineGraph-Dates">
                    <p>DD/MM</p>
                    <p>DD/MM</p>
                    <p>DD/MM</p>
                    <p>DD/MM</p>
                </div>
                </div>
            </div>
            </div>
        </div>
    );
};

const LineGraphComponent = (props) => {
    const canvasRef = useRef(null);
    const width = props.size[0];
    const height = props.size[0];
    const sections = props.sections;
    const intervals = width / (sections - 1);
    const incomingData = props.data;

    const lineGraphStyle = {
      width: `${width}`,
      border: '1px solid #ccc',
    };

    useEffect(() => {
      const canvas = canvasRef.current;
      const ctx = canvas.getContext('2d');

      var outgoingData = incomingData.map((entry, index) => ({
        x: index * intervals,
        y: height - entry.y,
      }));

    //   const maxY = Math.max(...outgoingData.map(entry => entry.y));
    //   console.log(`Max Number: ${maxY}`);

      ctx.clearRect(0, 0, width, height);
      ctx.beginPath();
      ctx.moveTo(outgoingData[0].x, outgoingData[0].y);

      for (let i = 1; i < outgoingData.length; i++) {
        ctx.lineTo(outgoingData[i].x, outgoingData[i].y);
      }

      ctx.strokeStyle = 'red';
      ctx.lineWidth = 5;
      ctx.stroke();
    }, [width, height, intervals, incomingData]);

    return <canvas ref={canvasRef} width={width} height={height} style={lineGraphStyle} />;
  };

const LineGraphScores = (props) => {

    const scoreRange = props.scoreRange;
    const scoreRangeMax = Math.max(...scoreRange);
    const scoreRangeMin = Math.min(...scoreRange);
    const scoreInterval = (scoreRangeMax-scoreRangeMin)/5

    return (
        <LineGraphScoresStyled>
            <p id='LineGraphScoreTop'>{scoreRangeMax}</p>
            <p>{scoreInterval*4}</p>
            <p>{scoreInterval*3}</p>
            <p>{scoreInterval*2}</p>
            <p>{scoreInterval*1}</p>
            <p id='LineGraphScoreBottom'>{scoreRangeMin}</p>
        </LineGraphScoresStyled>
    );
};

const LineGraphScoresStyled = styled.div`
    flex: 0 0 15%;
    background-color: pink;
    p {
        text-align: right;
        margin: 20px 0px;
    }
    #LineGraphScoreTop {
        margin-top: 0px;
    }
    #LineGraphScoreBottom {
        margin-bottom: 0px;
    }
`;

export default LineGraph;
