import React, { useEffect, useRef } from 'react';
import styled from 'styled-components';

const LineGraph = (props) => {

    // VARIABLES: Line Graph Container Properties
    const containerHeight = 400;
    const containerWidth = 400;

    // VARIABLES: Line Graph Mock Data
    const data = [
        {y: 0},
        {y: 11},
        {y: 33},
        {y: 50},
    ];
    const dates = [
        {dd: '08', mm: '04'},
        {dd: '09', mm: '05'},
        // {dd: '10', mm: '06'},
        // {dd: '11', mm: '07'},
    ];
    const size = [(containerWidth*.85), 250]; // (x, y) or (width, height)
    const sections = 4; // Number of entries.
    const scoreRange = [50, 200]; // (Minimum Score, Maximum Score)

    const lineGraphBackgroundStyle = {
        position: 'absolute',
        left: '-200%',
        marginTop: '50px',
        height: `${containerHeight}px`,
        width: `${containerWidth}px`,
        borderRadius: '25px',
        backgroundColor: 'rgb(235,235,235)',
    };

    return (
        <div id="LineGraph-Container" style={{display: 'flex', flexDirection: 'column', overflow: 'hidden'}}>
            <div className="LineGraph-Background" style={lineGraphBackgroundStyle}>
                {/* SUB-COMPONENT Part 1A/B */}
                <LineGraphScoreAndComponent scoreRange={scoreRange} componentData={data} componentSize={size} componentSection={sections}/>
                {/* SUB-COMPONENT Part 2 */}
                <LineGraphDates dates={dates}/>
            </div>
        </div>
    );
};

// SUB-COMPONENT: Part 1 - Combines y-axis and line graph component.
const LineGraphScoreAndComponent = (props) => {
    const range = props.scoreRange;
    const size = props.componentSize;
    const data = props.componentData;
    const sections = props.componentSection;
    return(
        <div className="LineGraph-Content-Row-1" style={{height: `${size[1]}px`, display: 'flex', flexDirection: 'row',}}>
            <LineGraphScore scoreRange={range}/>
            <LineGraphComponent data={data} size={size} section={sections}/>
        </div>
    );
};

// SUB-COMPONENT: Part 1A - Line graph component.
const LineGraphComponent = (props) => {

    // VARIABLES: Line Graph Display Properties
    const canvasRef = useRef(null);
    const width = props.size[0];
    const height = props.size[1];
    const sections = props.sections;
    const intervals = width / (sections - 1);
    const incomingData = props.data;

    // STYLE: Line Graph Component Stylings
    const lineGraphDisplayStyle = {
        flex: '0 0 85%',
        backgroundColor: 'blue',
        zIndex: '9999',
    };
    const lineGraphStyle = {
        width: `${width}`,
        border: '1px solid #ccc',
    };

    // GRAPH: Line Graph Creation
    useEffect(() => {
        const canvas = canvasRef.current;
        const ctx = canvas.getContext('2d');

        var outgoingData = incomingData.map((entry, index) => ({
            x: index * intervals,
            y: height - entry.y,
        }));

        console.log(outgoingData);
        // const maxY = Math.max(...outgoingData.map(entry => entry.y));
        // console.log(`Max Number: ${maxY}`);

        ctx.clearRect(0, 0, width, height);
        ctx.beginPath();
        ctx.moveTo(outgoingData[0].x, outgoingData[0].y);

        for (let i = 1; i < outgoingData.length; i++) {
        ctx.lineTo(outgoingData[i].x, outgoingData[i].y);
        }

        ctx.strokeStyle = 'red';
        ctx.lineWidth = 5;
        ctx.stroke();
    }, [width, height, intervals, incomingData]);

    return (
        <div className="LineGraph-Display" style={lineGraphDisplayStyle}>
            <canvas ref={canvasRef} width={width} height={height} style={lineGraphStyle} />
        </div>
    );
};

// SUB-COMPONENT: Part 1B - The y-axis.
const LineGraphScore = (props) => {

    const scoreRange = props.scoreRange;
    const scoreRangeMax = Math.max(...scoreRange);
    const scoreRangeMin = Math.min(...scoreRange);
    const scoreInterval = (scoreRangeMax-scoreRangeMin)/5

    return (
        <LineGraphScoreStyled>
            <p id='LineGraphScoreTop'>{scoreRangeMax}</p>
            <p>{scoreInterval*4}</p>
            <p>{scoreInterval*3}</p>
            <p>{scoreInterval*2}</p>
            <p>{scoreInterval*1}</p>
            <p id='LineGraphScoreBottom'>{scoreRangeMin}</p>
        </LineGraphScoreStyled>
    );
};

// STYLE: Part 1B - The y-axis stylings.
const LineGraphScoreStyled = styled.div`
    display: flex;
    flex-direction: column;
    flex: 0 0 15%;
    justify-content: space-between;
    background-color: pink;
    justify-content: space-between;
    p {
        text-align: right;
    }
`;

// SUB-COMPONENT: Part 2 - The x-axis.
const LineGraphDates = (props) => {

    const dates = props.dates;

    const datesStyle = {
        flex: '0 0 85%',
        display: 'flex',
        flexDirection: 'row',
        marginLeft: 'auto',
        backgroundColor: 'orange',
    };

    const checkDate = (index) => {
        if (dates[index] === undefined || dates[index] === null) {
            return false;
        } else {
            return true;
        }
    };

    return (
        <div className="LineGraph-Content-Row-2" style={{ display: 'flex', flexDirection: 'row' }}>
            <div className="LineGraph-Dates" style={datesStyle}>
                <p>{ checkDate(0) ? `${ dates[0].dd }/${ dates[0].mm }` : '-' }</p>
                <p>{ checkDate(1) ? `${ dates[1].dd }/${ dates[1].mm }` : '-' }</p>
                <p>{ checkDate(2) ? `${ dates[2].dd }/${ dates[2].mm }` : '-' }</p>
                <p>{ checkDate(3) ? `${ dates[3].dd }/${ dates[3].mm }` : '-' }</p>
            </div>
        </div>
    );
};

export default LineGraph;
