import React, { useEffect, useRef } from 'react';

const LineGraph = (props) => {
    const data = [
        {y: 0},
        {y: 50},
        {y: 65},
        {y: 100},
    ];

    const lineGraphScoresStyle = {
        flex: '0 0 75%',
        height: '250px',
        backgroundColor: 'blue',
        zIndex: '9999',
    };

  return (
    <div id="LineGraph-Container">
      <div className="LineGraph-Background">
        <div className="LineGraph-Content">
          <div className="LineGraph-Content-Row-1">
            <div className="LineGraph-Scores" style={lineGraphScoresStyle}>
              <p>50</p>
              <p>40</p>
              <p>30</p>
              <p>20</p>
              <p>10</p>
              <p>0</p>
            </div>
            <div className="LineGraph-Display">
              <LineGraphComponent data={data}/>
            </div>
          </div>
          <div className="LineGraph-Content-Row-2">
            <div className="LineGraph-Dates">
              <p>DD/MM</p>
              <p>DD/MM</p>
              <p>DD/MM</p>
              <p>DD/MM</p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

const LineGraphComponent = (props) => {
    const canvasRef = useRef(null);
    const width = 250;
    const height = 250;
    const sections = 4;
    const intervals = width / (sections - 1);
    const incomingData = props.data;

    const lineGraphStyle = {
      width: `${width}`,
      border: '1px solid #ccc',
    };

    useEffect(() => {
      const canvas = canvasRef.current;
      const ctx = canvas.getContext('2d');

      var outgoingData = incomingData.map((entry, index) => ({
        x: index * intervals,
        y: height - entry.y,
      }));

    //   const maxY = Math.max(...outgoingData.map(entry => entry.y));
    //   console.log(`Max Number: ${maxY}`);

      ctx.clearRect(0, 0, width, height);
      ctx.beginPath();
      ctx.moveTo(outgoingData[0].x, outgoingData[0].y);

      for (let i = 1; i < outgoingData.length; i++) {
        ctx.lineTo(outgoingData[i].x, outgoingData[i].y);
      }

      ctx.strokeStyle = 'red';
      ctx.lineWidth = 5;
      ctx.stroke();
    }, [width, height, intervals, incomingData]);

    return <canvas ref={canvasRef} width={width} height={height} style={lineGraphStyle} />;
  };


export default LineGraph;
